import { useState } from "react";
import Slide from "./Slide";

export default function Carousel() {
  const [activeImage, setActiveImage] = useState(1);
  const images = [IMAGE_1_URL, IMAGE_2_URL, IMAGE_3_URL];

  const goPrev = () => setActiveImage(activeImage === 1 ? 3 : activeImage - 1);
  const goNext = () => setActiveImage(activeImage === 3 ? 1 : activeImage + 1);

  return (
    <div className="carousel">
      <ul className="carousel__slides">
        {images.map((image, index) => (
          <Slide
            image={image}
            index={index + 1}
            activeImage={activeImage}
            key={index}
          />
        ))}
      </ul>
      <div className="carousel__controls">
        <label onClick={goPrev} className="carousel__slide-prev">
          <span>&lsaquo;</span>
        </label>
        <label onClick={goNext} className="carousel__slide-next">
          <span>&rsaquo;</span>
        </label>
      </div>
    </div>
  );
}
